import React, { useState, useEffect } from 'react';

const Buyers = () => {
  const [buyers, setBuyers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    mobile: '',
    gstNumber: '',
    email: '',
    cropsInterested: [],
    profilePhoto: ''
  });

  useEffect(() => {
    fetchBuyers();
  }, []);

  const fetchBuyers = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/buyers');
      const data = await response.json();
      setBuyers(data);
    } catch (err) {
      setError('Error fetching buyers: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCropsChange = (e) => {
    const crops = e.target.value.split(',').map(crop => crop.trim());
    setFormData({ ...formData, cropsInterested: crops });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/buyers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        fetchBuyers();
        setFormData({
          name: '',
          address: '',
          mobile: '',
          gstNumber: '',
          email: '',
          cropsInterested: [],
          profilePhoto: ''
        });
      } else {
        setError('Error creating buyer');
      }
    } catch (err) {
      setError('Error creating buyer: ' + err.message);
    }
  };

  if (loading) {
    return <div className="buyers"><h2>Buyers</h2><p>Loading buyers...</p></div>;
  }

  if (error) {
    return <div className="buyers"><h2>Buyers</h2><p>Error: {error}</p></div>;
  }

  return (
    <div className="buyers">
      <h2>Buyers</h2>
      <p>Find buyers interested in agricultural products.</p>

      <div className="form-section">
        <h3>Add New Buyer</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="address"
            placeholder="Address"
            value={formData.address}
            onChange={handleInputChange}
            required
          />
          <input
            type="tel"
            name="mobile"
            placeholder="Mobile Number"
            value={formData.mobile}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="gstNumber"
            placeholder="GST Number"
            value={formData.gstNumber}
            onChange={handleInputChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="cropsInterested"
            placeholder="Crops Interested (comma separated)"
            value={formData.cropsInterested.join(', ')}
            onChange={handleCropsChange}
          />
          <input
            type="url"
            name="profilePhoto"
            placeholder="Profile Photo URL"
            value={formData.profilePhoto}
            onChange={handleInputChange}
          />
          <button type="submit">Add Buyer</button>
        </form>
      </div>

      <div className="buyers-list">
        <h3>Existing Buyers</h3>
        {buyers.map((buyer) => (
          <div key={buyer._id} className="buyer-card">
            {buyer.profilePhoto && <img src={buyer.profilePhoto} alt={buyer.name} className="profile-photo" />}
            <h4>{buyer.name}</h4>
            <p><strong>Address:</strong> {buyer.address}</p>
            <p><strong>Mobile:</strong> {buyer.mobile}</p>
            <p><strong>GST:</strong> {buyer.gstNumber}</p>
            {buyer.email && <p><strong>Email:</strong> {buyer.email}</p>}
            <p><strong>Crops Interested:</strong> {buyer.cropsInterested.join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Buyers;
