import React, { useState, useEffect } from 'react';

const Sellers = () => {
  const [sellers, setSellers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    mobile: '',
    gstNumber: '',
    email: '',
    cropsAvailable: [],
    profilePhoto: ''
  });

  useEffect(() => {
    fetchSellers();
  }, []);

  const fetchSellers = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/sellers');
      const data = await response.json();
      setSellers(data);
    } catch (err) {
      setError('Error fetching sellers: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCropsChange = (e) => {
    const crops = e.target.value.split(',').map(crop => crop.trim());
    setFormData({ ...formData, cropsAvailable: crops });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/sellers', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        fetchSellers();
        setFormData({
          name: '',
          address: '',
          mobile: '',
          gstNumber: '',
          email: '',
          cropsAvailable: [],
          profilePhoto: ''
        });
      } else {
        setError('Error creating seller');
      }
    } catch (err) {
      setError('Error creating seller: ' + err.message);
    }
  };

  if (loading) {
    return <div className="sellers"><h2>Sellers</h2><p>Loading sellers...</p></div>;
  }

  if (error) {
    return <div className="sellers"><h2>Sellers</h2><p>Error: {error}</p></div>;
  }

  return (
    <div className="sellers">
      <h2>Sellers</h2>
      <p>Find sellers offering agricultural products.</p>

      <div className="form-section">
        <h3>Add New Seller</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="address"
            placeholder="Address"
            value={formData.address}
            onChange={handleInputChange}
            required
          />
          <input
            type="tel"
            name="mobile"
            placeholder="Mobile Number"
            value={formData.mobile}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="gstNumber"
            placeholder="GST Number"
            value={formData.gstNumber}
            onChange={handleInputChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="cropsAvailable"
            placeholder="Crops Available (comma separated)"
            value={formData.cropsAvailable.join(', ')}
            onChange={handleCropsChange}
          />
          <input
            type="url"
            name="profilePhoto"
            placeholder="Profile Photo URL"
            value={formData.profilePhoto}
            onChange={handleInputChange}
          />
          <button type="submit">Add Seller</button>
        </form>
      </div>

      <div className="sellers-list">
        <h3>Existing Sellers</h3>
        {sellers.map((seller) => (
          <div key={seller._id} className="seller-card">
            {seller.profilePhoto && <img src={seller.profilePhoto} alt={seller.name} className="profile-photo" />}
            <h4>{seller.name}</h4>
            <p><strong>Address:</strong> {seller.address}</p>
            <p><strong>Mobile:</strong> {seller.mobile}</p>
            <p><strong>GST:</strong> {seller.gstNumber}</p>
            {seller.email && <p><strong>Email:</strong> {seller.email}</p>}
            <p><strong>Crops Available:</strong> {seller.cropsAvailable.join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sellers;
